import React, { useState } from 'react';

export default function DataEntry(props) {
//   const [name, setName] = useState('');
//   const [grade, setGrade] = useState(0);
//   const [grad, setGrad] = useState(false);
//   const [error, setError] = useState('');



  const { name, grade, grad } = props; // Deconstruction


  const handleSaveClick = () => {
    verifyEntry();
  };

//   console.log('State:', name, grade, grad);

  return (
    <div className="dataEntry">
      <h2>Data Entry</h2>
      <div>
        <label htmlFor="NameInput">Name: </label>
        <input
          id="NameInput"
          value={name}
          onChange={(evt) => setName(evt.target.value)}
        />
      </div>
      <div>
        <label htmlFor="GradeInput">Grade: </label>
        <input
          id="GradeInput"
          value={grade}
          onChange={(evt) => setGrade(evt.target.value)}
        />
      </div>
      <div>
        <label htmlFor="GradCheckbox">Grad: </label>
        <input
          id="GradCheckbox"
          type="checkbox"
          checked={grad}
          onChange={(evt) => setGrad(evt.target.checked)}
        />
      </div>
      <div>
        <button onClick={handleSaveClick}>Save</button>
      </div>
      <div className="errors">
          {error}
      </div>
      <hr />
    </div>
  );
}
